class Solution:
    def maxOperations(self, s: str) -> int:
        operations = 0
        ones = 0
        prev = ''

        # Traverse the string from left to right
        for char in s:
            if char == '1':
                ones += 1
            elif char == '0' and prev == '1':
                if ones > 0:
                    operations += ones
            prev = char

        return operations
