class Solution {
public:
    int maxOperations(string s) {
        int ans=0,cnt=0;
        bool flag=false;
        for (int i=0;i<s.length();i++)
            if (s[i]=='0') flag=true;
            else{
                if (flag){
                    ans+=cnt;
                    flag=false;
                }
                cnt++;
            }
        if (flag) ans+=cnt;
        return ans;
    }
};
