#define ll long long int
class Solution {
public:
    int maxOperations(string s) {
        ll n=s.length();
        vector<pair<char,ll>> v;
        char cur=s[0];
        ll track=1;
        for(ll i=1;i<n;i++){
            if(s[i]==cur){
                track++;
            }else{
                v.push_back({cur,track});
                cur=s[i];
                track=1;
            }
        }
        v.push_back({cur,track});
        ll add=0;
        ll ans=0;
        for(ll i=0;i<v.size();i++){
            if(v[i].first=='0'){
                ans=ans+add;
            }else{
                add=add+v[i].second;
            }
        }
        return ans;
    }
};
