class Solution {
public:
    int maxOperations(string s) {
        int n = s.length();
        bool flag = 1;
        int c = 0, res=0;
        for(int i = n-1; i>=0; i--){
            if(flag && s[i] == '0'){
                flag = 0;
                c++;
            }
            else if(!flag && s[i] == '0'){
                continue;
            }
            else if(s[i] == '1'){
                flag = 1;
                res += c;
            }
        }
        return res;
    }
};
