class Solution {
    public int maxOperations(String s) {
        int cur = 0;
        int ans = 0;
        boolean sign = true;
        for(int i=0;i<s.length();i++){
            // 不断向后移动，统计出现过多少次的1
            if(s.charAt(i)=='1'){
                cur++;
                sign = true;
            } // 如果连续的1第一次遇到0，那么前面统计过的所有1都能向右移动一次，所以ans加上cur，注意，多个0也只能移动一次
            else{ // 这里不用关心右边有没有1，只要有0就能移动
                if(sign){
                    ans +=cur;
                }
                sign = false;
            }
        }
        return ans;
    }   
}
