class Solution {
public:
    int maxOperations(string s) {
        int n = s.size(), ans = 0, group=0;
        bool scored = false;
        for(int i=0;i<n;i++) {
            if(s[i]=='1') {
                group++;
                scored = false;
            } else if (s[i]=='0' && !scored) {
                ans += group;
                scored = true;
            }
        }
        return ans;
    }
};
