class Solution {
    public int maxOperations(String s) {
        int countOnes = 0;
        int steps = 0;
        for(char c : s.toCharArray()){
            if(c == '1'){
                countOnes += 1;
            }
        }
        for(int i = s.length() - 1; i >= 0; i--){
            if(s.charAt(i) == '1'){
                countOnes -= 1;
                continue;
            }
            if(i != s.length() - 1 && s.charAt(i) == s.charAt(i+1)){
                continue;
            }
            steps += countOnes;
        }
        return steps;
    }
}
