class Solution {
    public int maxOperations(String s) {
        int count1 = 0;
        int ans = 0;
        for (int i = 0; i < s.length();i++) {
            if (s.charAt(i) == '1') {
                count1++;
            }
            else {
                while (i < s.length() -1 && s.charAt(i+1) == '0') {
                    i++;
                }
                ans += count1;
            }
        }
        //if (s.charAt(s.length()-1) == '0') {
        //    ans += count1;
       // /}
        if (count1 == 0 || count1 == s.length()) {
            return 0;
        }
        return ans;
    }
}
