class Solution:
    def maxOperations(self, s: str) -> int:
        n=len(s)
        memo=[]
        for i,c in enumerate(s):
            if c=='1':memo.append(i)
        @cache
        def dp(i):
            if i==len(memo)-1:return int(memo[-1]!=n-1)
            return dp(i+1)+int(memo[i+1]-memo[i]>1)
        return sum(dp(i) for i in range(len(memo)))
