/**
 * @param {string} s
 * @return {number}
 */
var maxOperations = function(s) {
    let curr = 0, ans = 0, zero = 0;
    for(let i=s.length-1; i>=0; i--){
        let c = s[i];
        if(c === '0'){
            zero++;
        }else{
            if(c === '1'){
                if(zero > 0){
                    curr++;
                }
                ans += curr;
            }
            zero = 0;
        }
    }
    return ans;
};
