class Solution {
public:
    int maxOperations(string s) {
       int res = 0;
        int k = 0;
        bool flag = false;
        vector<int> arr;


        for (auto& ch : s) {
            if (ch == '1') {
                arr.push_back(1);
                flag = true;
            } else if (ch == '0' && flag) {
                arr.push_back(0);
                flag = false;
            }
        }


        for (int bit : arr) {
            if (bit == 1) {
                k += 1;
            } else {
                res += k;
            }
        }

        return res;
    }
};
