class Solution:
    def maxOperations(self, s: str) -> int:
        i = 0
        n = len(s)
        ans = 0
        prevOneCount = 0
        while i < n:
            char = s[i]
            if char == '1':
                j = i
                while i < n and s[i] == "1":
                    i += 1

                if i != n:
                    ans += prevOneCount + i - j
                    prevOneCount += i - j
            i += 1
        return ans
