class Solution {
public:
    int maxOperations(string s) {
        s += '1';
        int c = 0, ans = 0;
        int o0 = 0;
        for(char x : s) {
            if(x == '0') o0 = 1;
            else {
                if(o0) {
                    ans += c;
                } 
                c ++;
                o0 = 0;
            }
        }
        return ans;
    }
};
