class Solution {
public:
    int maxOperations(string s) {
        std::string t;
        int i = 0;
        int n = s.length();
        while (i < n) {
            int i0 = i;
            if (s[i0] == '1') {
                i++;
                t.push_back('1');
                continue;
            }
            while (i < n && s[i] == '0') {
                i++;
            }
            t.push_back('0');
        }
        n = t.length();
        std::vector<int> suf(n + 1);
        for (int i = n - 1; i >= 0; i--) {
            suf[i] = suf[i + 1] + (t[i] == '0');
        }
        int ans = 0;
        for (int i = 0; i < n; i++) {
            if (t[i] == '1') {
                ans = ans + suf[i];
            }
        }
        return ans;
    }
};
