class Solution:
    def maxOperations(self, s: str) -> int:
        res = [0]
        prv = '0'
        for c in s:
            if c == '1':
                res[-1] += 1
            else:
                if prv == '0':
                    continue
                else:
                    res.append(0)
            prv = c
        print(res)

        s = 0
        for i, x in enumerate(res[::-1]):
            s += i * x
        return s
