class Solution {
public:
    int maxOperations(string s) {
        int moves = 0;
        int contOnes = 0;
        int n = s.length();
        for (int i = 0; i < n; i++) {
            char c = s[i];
            if (c == '1') {
                contOnes++;
            } else {
                if (i == n - 1 || s[i + 1] == '1') {
                    moves += contOnes;
                }
            }
        }
        return moves;
    }
};
