class Solution {
public:
    int maxOperations(string s) {
        int op=0;
        int  ct=0;
        int n=s.size();
        for(int i=0;i<n;i++){
            if(s[i]=='1'){
                ct++;
            }
            if(s[i]=='0' && ct){
                // cout<<i<<endl;
                while(i<n && s[i]=='0')i++;
                i--;
                op+=ct;
            }
        }
        return op;
    }
};
