class Solution:
    def maxOperations(self, s: str) -> int:
        ans = 0
        num_ones = 0
        zero_counted = False

        for c in s:
            if c == '1':
                num_ones += 1
                zero_counted = False
            if c == '0' and not zero_counted:
                ans += num_ones
                zero_counted = True

        return ans
