class Solution {
public:
    long long maxOperations(string s) {
        string s2 = "";
        char prv='-';
        for(char c:s) {
            if(prv=='0' && c=='0')
                continue;
            s2+=c;
            prv=c;
        }
        long long ans = 0;
        int cnt=0;
        for(int i=(int)s2.size()-1;i>=0;i--) {
            if(s2[i]=='1')
                ans+=cnt;
            else
                cnt++;
        }
        return ans;
    }
};
