class Solution {
public:
    int maxOperations(string s) {
        int ans = 0;
        int tmp = 0;
        int n = s.size();
        for (int i = n - 1; i >=0 ;i--) {
            if (s[i] == '1') {
                ans += tmp;
            } else if (s[i] == '0' && (i == n-1 || s[i + 1] == '1')) {
                tmp ++;
            }
        }
        return ans;
    }
};
