class Solution {
public:
    int maxOperations(string s) {
        int cnt1=0,ans=0;
        for(int i=0;i<s.size();i++){
            if(s[i]=='1') cnt1++;
            else if(i<s.size()-1 && s[i]=='0' && s[i+1]=='1') ans+=cnt1;
            else if(i==s.size()-1 && s[i]=='0') ans+=cnt1;
        }
        return ans;
    }
};
