class Solution:
    def maxOperations(self, s: str) -> int:
        s += "1"
        res = 0
        sum1 = 0
        add = False
        for i in range(len(s) - 1):
            sum1 += int(s[i])
            if s[i] == "1":
                add = True
            if s[i + 1] == "0" and add:
                #print("non")
                add = False
                res += sum1
        return res
