class Solution {
    public int maxOperations(String s) {
        int n = s.length();
        int ans = 0;
        int cnt = 0;
        for(int i = n-2; i >= 0; i--){
            if(s.charAt(i) == '1' && s.charAt(i+1) == '0'){
                cnt++;
            }
            if(s.charAt(i) == '1') ans += cnt;
        }
        return ans;
    }
}
