class Solution {
    public int maxOperations(String s) {
        int ans = 0;
        int zeros = 0;
        boolean lastZero = false;
        for (int i = s.length() -1; i >= 0 ; i--) {
            if (s.charAt(i) == '0') {
                if (!lastZero) {
                    zeros++;
                    lastZero = true;
                }
            } else {
                ans += zeros;
                lastZero = false;
            }
        }
        return ans;
    }
}
