class Solution {
public:
    int maxOperations(string s) {
        int cnt = 0;
        int zero = 0;
        int n = s.length();
        int ans = 0;
        string temp = "";
        for(int i = 0 ; i < n ; i++){
            if(s[i] == '0'){
                zero++;
            }
            else{
                if(zero > 0){
                    ans += cnt;
                    zero = 0;
                }
                cnt++;
            }
        }
        if(zero > 0){
            ans += cnt;
        }
        return ans;
    }
};
