class Solution {
public:
    int maxOperations(string s) {
        int c=0;int o=0;
        // cout<<s.length();
        for(int i=0;i<s.length();i++){
            int t=c;
            // cout<<i<<endl;
            if(s[i]=='1'){
                t++;
                o+=c;
                while(i+1<s.length()&&s[i+1]!='0'){
                    i++;t++;
                }
                if(i>=s.length()-1) return o;
                c=t;
                // cout<<c<<" "<<o<<endl;
            }

            // i++;
        }
        return o+c;
    }
};
