const int N = 1e3 + 2, mod = 1e9 + 7; //= 998244353;
class Solution {
public:
    int maxOperations(string s) {
        int n=s.size();
        int zero=0;
        int res=0;
        for(int i=n-1;i>=0;i--){
            if(s[i]=='0'){
                int j=i;
                while(j-1>=0&&s[j-1]=='0')j--;
                zero++;
                i=j;
            }
            else res+=zero;
        }
        return res;
    }
};
