class Solution {
public:
    int maxOperations(string s) {
        int n = s.size();

        vector<int> v;
        for (int i = 0; i < n; i++) {
            if (s[i] == '0') {
                continue;
            }
            // s[i] == '1'
            int j = i + 1;
            while (j < n && s[j] == s[i]) {
                j++;
            }
            v.push_back(j - i);
            i = j - 1;
        }
        if (s[n - 1] == '0') {
            v.push_back(0);
        }

        int cnt = 0, ans = 0;
        for (int i = v.size() - 1; i >= 0; i--) {
            ans += cnt * v[i];
            cnt++;
        }
        return ans;
    }
};
