class Solution {
public:
    int maxOperations(string s) {
        int z = 0;
    int op= 0;
    int k=0;
   int n=s.size();
    for (int i=0;i<n-1;i++ ) {
        if(s[i]=='1')
        {
            z++;
            k=1;
        }
        if(s[i]=='0' && s[i+1]=='1' && k==1)
        {
            op+=z;
        }
    }
    if(s[n-1]=='0')
    op+=z;

    return op;
    }
};
