class Solution:
    def maxOperations(self, s: str) -> int:
        n = len(s)
        zero,one,op,i=0,0,0,0
        while i<n:
            if s[i]=='0':
                while i<n-1 and s[i]==s[i+1]:
                    i+=1
                op+=one
            else:
                one+=1
            # print(i,one,op)
            i+=1
        return op
