class Solution {
public:
    int maxOperations(string s) {
        int cntL=0;
        int res=0;
        for (int i=0; i<s.size()-1; ++i) {
            if (s[i]=='1') {
                cntL++;
                if (s[i+1]=='0') {
                    res+=cntL;
                }
            }
        }
        return res;
    }
};
