/**
 * @param {string} s
 * @return {number}
 */
var maxOperations = function(s) {

    // 贪心
    let lastIndex = -1, lastCnt = 0, ans = 0
    for (let i = 0; i < s.length; i++) {
        if (s[i] === '0') continue
        if (lastIndex === -1) {
            lastIndex = i
            lastCnt++
        }
        // 把前面的都操作到当前i的前面， 注意：前提与lastIndex之间至少包含一个0
        else if (i - lastIndex == 1) {
            lastIndex = i
            lastCnt++
        }
        // 可以移动
        else {
            ans += lastCnt
            lastIndex = i
            lastCnt++
        }
    }
    // 如果最后一个元素是0，还可以在移动一次
    if (lastIndex < s.length - 1) {
        ans += lastCnt
    }



    return ans
};
