class Solution:
    def maxOperations(self, s: str) -> int:
        ans = 0
        n = len(s)
        cnt = 0
        ind = -1
        for i in range(n):
            if s[i]=='0':continue
            if i-ind==1:
                cnt+=1
            else:
                ans+=cnt
                cnt+=1
            ind  = i
        if s[-1]=='0':ans+=cnt
        return ans
