class Solution {
public:
    int maxOperations(string s) {
        int maxOps = 0;
        int zeroCount = 0;
        int ones = 0;

        for (char ch : s) {
            if (ch == '0') {
                zeroCount++;
            } else {
                if (ones && zeroCount > 0) {
                    maxOps += ones;
                }
                ones ++;
                zeroCount = 0;
            }
        }
        if(s[s.size() - 1] == '0') {
            maxOps += ones;
        }

        return maxOps;
    }
};
