class Solution:
    def maxOperations(self, s: str) -> int:
        vec = []
        current = 1
        for i in range(len(s)):
            if s[i] == '1':
                vec.append(current)
                current+=1
            else:
                vec.append(0)
        i = len(s)-1
        step = 0
        while i>=1:
            if vec[i] == 0 and vec[i-1] != 0:
                step += vec[i-1]
            i-=1
        return step
