class Solution {
    public int maxOperations(String s) {
        // 最大操作次数
        // 就是从左往右开始选,直到移不动了,最右全是1
        int n = s.length();
        char[] sc = s.toCharArray();
        int[] pre = new int[n + 1];// 第i个数前有几个1
        pre[0] = 0;
        for (int i = 1; i < n + 1; i++) {
            pre[i] = pre[i - 1] + (sc[i - 1] == '1' ? 1 : 0);
        }
        int cnt = 0;
        for (int i = 0; i < n; i++) {
            if (i > 0 && sc[i - 1] == sc[i] && sc[i] == '0') {
                continue;
            }
            if (sc[i] == '0') {
                cnt += pre[i + 1];
            }
        }
        return cnt;
    }

    public void swap(char[] sc, int i, int j) {
        char temp = sc[i];
        sc[i] = sc[j];
        sc[j] = temp;
    }
}
