class Solution {
public:
    int maxOperations(string str) {
        vector<int> a;
        int n = str.size();
        int i = 0;
        while (i < n) {
            if (str[i] == '1') {
                int left = i;
                while (i < n && str[i] == '1') {
                    i++;
                }
                int cnt = i - left;
                if (i < n && str[i] == '0') {
                    a.push_back(cnt);
                    i++;
                }
            } else {
                i++;
            }
        }
        // for (int x : a) {
        //     cout << x << " ";
        // }
        int m = a.size();
        for (int i = 1; i < m; i++) {
            a[i] += a[i - 1];
        }
        return accumulate(a.begin(), a.end(), 0);
    }
};
