class Solution {
public:
    int maxOperations(string s) {
       int a=0;
       int b=0;
       bool d=0;
       vector<int>h;
       for(int i=0;i<s.size();i++){
        if(s[i]=='1'){
            h.push_back(1);
            d=1;
        }
        if(s[i]=='0'&& d==1){
            h.push_back(0);
            d=0;
        }
       }
       for(auto i:h){
        if(i==1){
            b++;
        }
        else{
            a=a+b;
        }
       }
       return a;
    }
};
