class Solution {
public:
    int maxOperations(string s) {

        int n=s.length();
        int c=0;
        int ans=0;
        bool f=false;
        for(int i=0;i<n;i++)
        {
            if(s[i]=='1')
            { c++;
            f=true;
            }
            else{
                if(f)
                {ans+=c;
                f=false;}

            }
        }
        return ans;
    }
};
