class Solution {
public:
    int maxOperations(string s) {
        s.push_back('1');
        int n = s.size();
        long long ret = 0;
        for (int i = n - 1, cnt = 0; i >= 0; i--) {
            if (s[i] == '1') {
                int j = i;
                while (j >= 0 && s[j] == '1') {
                    j--;
                }
                ret += 1ll * cnt * (i - j);
                cnt++;
                i = j + 1;
            }
        }
        return ret;
    }
};
