class Solution {
    public int maxOperations(String s) {
        int n=s.length();
        int res=0;
        int last=n-1;
        while(last>=0&&s.charAt(last)=='0'){
            last--;
        }
        if(last==-1){
            return 0;
        }
        List<Integer> list=new ArrayList();
        for(int i=0;i<n;i++){
            if(s.charAt(i)=='1'){
                int j=i;
                while(j<n&&s.charAt(j)=='1'){
                    j++;
                }

                list.add(j-i);
                i=j-1;
            }
        }
        // for(int v:list){
        //     System.out.println(v);
        // }
        int size=list.size();
        int c=s.charAt(n-1)=='1'?0:1;
        for(int i=size-1;i>=0;i--){
            res+=list.get(i)*c;
            c++;
        }

        return res;

    }
}
