class Solution
{
public:
    int maxOperations(string s)
    {
        int n = s.size();
        vector<int> dp(n, 0);
        int bol = 0;
        int ct = 0;
        if (s[0] == '1')
        {
            ct++;
        }
        int ans = 0;
        for (int i = 1; i < n; i++)
        {
            if (s[i - 1] == '0' && s[i] == '1')
            {
                ans += ct;
            }
            if (s[i] == '1')
            {
                ct++;
            }
        }
        if(s[n-1]=='0'){
            ans+=ct;
        }
        return ans;
    }
};
