class Solution {
public:
    int maxOperations(string s) {
        int ans=0;
        int p=0;
        while(s[p]==0){
            p++;
        }
        int count=0;
        for(int i=p;i<s.size();i++){
            if(s[i]=='1'){
                count++;
            }
            bool check=false;
            while(i<s.size()&&s[i]=='0'){
                check=true;
                i++;
            }
            if(check){
                i--;
                ans+=count;
            }
        }
        return ans;
    }
};
