class Solution:
    def maxOperations(self, s: str) -> int:
        n = len(s)
        ans = 0
        cnt = 0
        i = 0
        while i < n:
            while i < n and s[i] == '0':
                i += 1
            if i == n: break

            while i < n and s[i] == '1':
                i += 1
                cnt += 1
            if i < n:
                ans += cnt
        return ans
