class Solution {
    public int maxOperations(String s) {
        int n = s.length();
        int t = 0;
        List<Integer> a = new ArrayList<>();
        for (int i=0; i<n; i++) {
            if (s.charAt(i) == '1')
                t++;
            else if (t > 0) {
                a.add(t);
                t = 0;
            }
        }
        int m = a.size();
        int ans = 0;
        int z = 0;
        for (int i=0; i<m; i++) {
            z += a.get(i);
            ans += z;
        }
        return ans;
    }
}
