class Solution {
public:
    int maxOperations(string s) {
        int ans=0,ct=0;
        int n=s.size();
        for(int i=n-1;i>=0;)
        {
            if(s[i]=='0')
            {
                ++ct;
                while(i>=0 && s[i]=='0')
                {
                    --i;
                }
            }
            else
            {
                ans+=ct;
                --i;
            }
        }
        return ans;
    }
};
