class Solution {
    public int maxOperations(String s) {
        ArrayList<Integer> list = new ArrayList<>();
        int cnt = 0;
        for(int i = 0; i < s.length() ; i ++){
            if(s.charAt(i) == '1'){
                cnt++;
            }else{
                if(cnt != 0){
                    list.add(cnt);
                }

                cnt = 0;
            }
        }
        int res = 0;
        // System.out.print(list.size());
        for (int i = 0; i < list.size(); i++) {
            res += list.get(i) * (list.size() - i);
        }
        return res;
    }
}
