class Solution {
public:
    int maxOperations(string s) {
        int n = s.length();
        int ans = 0;
        vector<int> p;
        for (int i = 0; i < n; i++) {
            if (s[i] == '1') p.push_back(i);
        }
        p.push_back(n);
        int m = p.size();
        vector<int> cnt(m, 1);
        for (int i = 0; i < m - 1; i++) {
            if (p[i] != p[i + 1] - 1) ans += cnt[i];
            cnt[i + 1] += cnt[i];
        } 
        return ans;
    }
};
