class Solution:
    def maxOperations(self, s: str) -> int:
        i=len(s)-1
        dic={}
        while(s[i]=="1" and i>=0):
            i-=1
        while(i>=0):
            if s[i]=="1":
                dic[i]=1
                t=i
                while(i>0 and s[i-1]=="1") :
                    dic[t]+=1
                    i-=1
            i-=1
        vals=sorted(list(dic.keys()))
        ans=0
        # print(dic)
        for i in range(len(vals)-1,-1,-1):
            ans+=(len(vals)-i)*dic.get(vals[i],0)
            print(ans)
        return ans
