class Solution {
    public int maxOperations(String s) {
        boolean f = false;
        int n = s.length();
        int cnt = 0, ans = 0;
        for(int i = 0; i < n; i++){
            char ch = s.charAt(i);
            if(ch == '1'){
                cnt++;
                f = true;
            }else{
                if(f){
                    ans += cnt;
                    f = false;
                }
            }
        }
        return ans;
    }
}
